import React, { useState, useEffect } from 'react';
import { courseService } from '../services/courseService';

const CourseList = ({ onSelectCourse, onEditCourse, onDeleteCourse }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredCourses, setFilteredCourses] = useState([]);

  // Fetch courses on component mount
  useEffect(() => {
    fetchCourses();
  }, []);

  // Filter courses based on search term
  useEffect(() => {
    if (searchTerm.trim() === '') {
      setFilteredCourses(courses);
    } else {
      const filtered = courses.filter(course =>
        course.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        course.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredCourses(filtered);
    }
  }, [courses, searchTerm]);
  const fetchCourses = async () => {
    try {
      setLoading(true);
      const coursesData = await courseService.getAllCourses();
      setCourses(coursesData);
      setError('');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (courseId) => {
    if (window.confirm('√ätes-vous s√ªr de vouloir supprimer ce cours ?')) {
      try {
        await courseService.deleteCourse(courseId);
        setCourses(courses.filter(course => course.id !== courseId));
        if (onDeleteCourse) onDeleteCourse(courseId);
      } catch (err) {
        setError(err.message);
      }
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('fr-FR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };
  if (loading) {
    return (
      <div className="p-5 max-w-7xl mx-auto">
        <div className="flex flex-col items-center justify-center py-16">
          <div className="relative w-16 h-16 mb-5">
            {/* Nouveau spinner avec animation Tailwind */}
            <div className="absolute top-0 left-0 w-full h-full border-4 border-blue-100 rounded-full"></div>
            <div className="absolute top-0 left-0 w-full h-full border-4 border-transparent border-t-blue-600 rounded-full animate-spin"></div>
            <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-10 h-10 bg-white rounded-full flex items-center justify-center">
              <div className="w-6 h-6 text-blue-600">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                </svg>
              </div>
            </div>
          </div>
          <p className="text-gray-600 font-medium">Chargement des cours...</p>
        </div>
      </div>
    );
  }
  return (
    <div className="p-5 max-w-7xl mx-auto">
      <div className="flex justify-between items-center mb-8 flex-wrap gap-5">
        <h2 className="text-2xl font-semibold text-gray-800 m-0">Liste des Cours</h2>
        <div className="flex-1 max-w-md">
          <input
            type="text"
            placeholder="Rechercher un cours..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full py-3 px-4 border-2 border-gray-200 rounded-lg text-base transition-colors focus:outline-none focus:border-blue-500 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
          />
        </div>
      </div>

      {error && <div className="bg-red-100 text-red-800 p-4 rounded-lg mb-5 border border-red-200">{error}</div>}

      {filteredCourses.length === 0 ? (
        <div className="no-courses">
          <p>Aucun cours trouv√©.</p>
        </div>
      ) : (
        <div className="courses-grid">
          {filteredCourses.map((course) => (
            <div key={course.id} className="course-card">
              <div className="course-header">
                <h3 
                  className="course-title" 
                  onClick={() => onSelectCourse && onSelectCourse(course)}
                >
                  {course.title}
                </h3>
                <div className="course-actions">
                  <button
                    className="btn-edit"
                    onClick={() => onEditCourse && onEditCourse(course)}
                    title="Modifier"
                  >
                    ‚úèÔ∏è
                  </button>
                  <button
                    className="btn-delete"
                    onClick={() => handleDelete(course.id)}
                    title="Supprimer"
                  >
                    üóëÔ∏è
                  </button>
                </div>
              </div>
              
              <div className="course-description">
                <p>{course.description}</p>
              </div>
              
              <div className="course-footer">
                <div className="course-dates">
                  <small>Cr√©√© le {formatDate(course.createdAt)}</small>
                  {course.updatedAt !== course.createdAt && (
                    <small>Modifi√© le {formatDate(course.updatedAt)}</small>
                  )}
                </div>
                
                {course.employees && course.employees.length > 0 && (
                  <div className="course-enrollment">
                    <span className="enrollment-count">
                      {course.employees.length} inscription(s)
                    </span>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CourseList;
