import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8000/api';

// Create axios instance with default config
const courseAPI = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add interceptor to include JWT token
courseAPI.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Course service functions
export const courseService = {
  // Get all courses
  getAllCourses: async () => {
    try {
      const response = await courseAPI.get('/courses');
      return response.data;
    } catch (error) {
      throw new Error('Erreur lors de la récupération des cours');
    }
  },

  // Get course by ID
  getCourseById: async (id) => {
    try {
      const response = await courseAPI.get(`/courses/${id}`);
      return response.data;
    } catch (error) {
      throw new Error('Erreur lors de la récupération du cours');
    }
  },

  // Create new course
  createCourse: async (courseData) => {
    try {
      const response = await courseAPI.post('/courses', courseData);
      return response.data;
    } catch (error) {
      throw new Error('Erreur lors de la création du cours');
    }
  },

  // Update course
  updateCourse: async (id, courseData) => {
    try {
      const response = await courseAPI.put(`/courses/${id}`, courseData);
      return response.data;
    } catch (error) {
      throw new Error('Erreur lors de la mise à jour du cours');
    }
  },

  // Delete course
  deleteCourse: async (id) => {
    try {
      await courseAPI.delete(`/courses/${id}`);
      return true;
    } catch (error) {
      throw new Error('Erreur lors de la suppression du cours');
    }
  },

  // Search courses
  searchCourses: async (searchTerm) => {
    try {
      const response = await courseAPI.get(`/courses?search=${encodeURIComponent(searchTerm)}`);
      return response.data;
    } catch (error) {
      throw new Error('Erreur lors de la recherche de cours');
    }
  }
};

export default courseService;
