import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

// JWT Token utilities
const JWT_TOKEN_KEY = 'jwt_token';
const JWT_REFRESH_TOKEN_KEY = 'jwt_refresh_token';
const USER_DATA_KEY = 'user_data';

const jwtUtils = {
  // Store tokens
  setTokens: (token, refreshToken = null) => {
    localStorage.setItem(JWT_TOKEN_KEY, token);
    if (refreshToken) {
      localStorage.setItem(JWT_REFRESH_TOKEN_KEY, refreshToken);
    }
  },

  // Get access token
  getToken: () => {
    return localStorage.getItem(JWT_TOKEN_KEY);
  },

  // Get refresh token
  getRefreshToken: () => {
    return localStorage.getItem(JWT_REFRESH_TOKEN_KEY);
  },

  // Remove all tokens
  clearTokens: () => {
    localStorage.removeItem(JWT_TOKEN_KEY);
    localStorage.removeItem(JWT_REFRESH_TOKEN_KEY);
    localStorage.removeItem(USER_DATA_KEY);
  },

  // Check if token is expired
  isTokenExpired: (token) => {
    if (!token) return true;
    
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      const currentTime = Date.now() / 1000;
      return payload.exp < currentTime;
    } catch (error) {
      return true;
    }
  },

  // Decode token payload
  decodeToken: (token) => {
    if (!token) return null;
    
    try {
      return JSON.parse(atob(token.split('.')[1]));
    } catch (error) {
      return null;
    }
  }
};

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState(null);
  useEffect(() => {
    // Check if user is logged in on app load
    const storedToken = jwtUtils.getToken();
    const userData = localStorage.getItem(USER_DATA_KEY);
    
    if (storedToken && userData) {
      // Check if token is still valid
      if (!jwtUtils.isTokenExpired(storedToken)) {
        setToken(storedToken);
        setIsAuthenticated(true);
        setUser(JSON.parse(userData));
        
        // Set up axios interceptor for this token
        setupAxiosInterceptors(storedToken);
      } else {
        // Token expired, try to refresh or logout
        handleTokenExpired();
      }
    }
    setLoading(false);
  }, []);

  // Setup axios interceptors for JWT
  const setupAxiosInterceptors = (authToken) => {
    // Request interceptor to add JWT token
    axios.interceptors.request.use(
      (config) => {
        if (authToken) {
          config.headers.Authorization = `Bearer ${authToken}`;
        }
        return config;
      },
      (error) => {
        return Promise.reject(error);
      }
    );

    // Response interceptor to handle token expiration
    axios.interceptors.response.use(
      (response) => response,
      async (error) => {
        const originalRequest = error.config;

        if (error.response?.status === 401 && !originalRequest._retry) {
          originalRequest._retry = true;
          
          // Try to refresh token
          const refreshToken = jwtUtils.getRefreshToken();
          if (refreshToken) {
            try {
              const newToken = await refreshAccessToken(refreshToken);
              if (newToken) {
                originalRequest.headers.Authorization = `Bearer ${newToken}`;
                return axios(originalRequest);
              }
            } catch (refreshError) {
              handleTokenExpired();
            }
          } else {
            handleTokenExpired();
          }
        }
        
        return Promise.reject(error);
      }
    );
  };

  // Handle token expiration
  const handleTokenExpired = () => {
    jwtUtils.clearTokens();
    setToken(null);
    setIsAuthenticated(false);
    setUser(null);
  };

  // Refresh access token
  const refreshAccessToken = async (refreshToken) => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_BASE_URL}/auth/refresh`, {
        refresh_token: refreshToken
      });
      
      const { token: newToken, refresh_token: newRefreshToken } = response.data;
      jwtUtils.setTokens(newToken, newRefreshToken);
      setToken(newToken);
      
      return newToken;
    } catch (error) {
      throw error;
    }
  };

  const login = async (credentials) => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_BASE_URL}/auth/login`, credentials);
      
      const { token: authToken, refresh_token: refreshToken, user: userData } = response.data;
      
      // Store tokens and user data
      jwtUtils.setTokens(authToken, refreshToken);
      localStorage.setItem(USER_DATA_KEY, JSON.stringify(userData));
      
      // Update state
      setToken(authToken);
      setIsAuthenticated(true);
      setUser(userData);
      
      // Setup axios interceptors
      setupAxiosInterceptors(authToken);
      
      return { success: true, user: userData };
    } catch (error) {
      const message = error.response?.data?.message || 'Erreur de connexion';
      return { success: false, error: message };
    }
  };

  const logout = () => {
    jwtUtils.clearTokens();
    setToken(null);
    setIsAuthenticated(false);
    setUser(null);
    
    // Clear axios interceptors
    axios.interceptors.request.clear();
    axios.interceptors.response.clear();
  };

  // Get current JWT token
  const getToken = () => {
    return token || jwtUtils.getToken();
  };

  // Check if user has specific role
  const hasRole = (role) => {
    if (!user || !user.roles) return false;
    return user.roles.includes(role);
  };

  // Check if user is admin
  const isAdmin = () => {
    return hasRole('ROLE_ADMIN');
  };

  // Check if user is employee
  const isEmployee = () => {
    return hasRole('ROLE_EMPLOYEE') || hasRole('ROLE_EMPLOYÉE');
  };

  const value = {
    isAuthenticated,
    user,
    token,
    login,
    logout,
    loading,
    getToken,
    hasRole,
    isAdmin,
    isEmployee,
    refreshAccessToken
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
